import React, { useState, useEffect } from 'react';
import axios from 'axios';

const DefaultersPage = () => {
  const currentDate = new Date();
  const [defaulters, setDefaulters] = useState([]);
  const [loading, setLoading] = useState(true);
  const [month, setMonth] = useState(currentDate.getMonth() + 1);
  const [year, setYear] = useState(currentDate.getFullYear());

  const months = [
    'January', 'February', 'March', 'April', 'May', 'June',
    'July', 'August', 'September', 'October', 'November', 'December',
  ];

  const years = [];
  for (let y = 2023; y <= 2030; y++) {
    years.push(y);
  }

  useEffect(() => {
    fetchDefaulters();
  }, [month, year]);

  const fetchDefaulters = async () => {
    setLoading(true);
    try {
      const response = await axios.get(`http://localhost:4000/api/defaulters/${month}/${year}`);
      setDefaulters(response.data.students || []);
    } catch (error) {
      console.error('Error fetching defaulters:', error);
    } finally {
      setLoading(false);
    }
  };

  const downloadCSV = () => {
    const headers = 'GR No, Roll No, Name, Attendance %\n';
    const rows = defaulters.map(student =>
      `${student.gr_no}, ${student.roll_no}, ${student.name}, ${student.attendance_percentage}`
    ).join('\n');

    const csvContent = headers + rows;
    const blob = new Blob([csvContent], { type: 'text/csv' });
    const downloadUrl = URL.createObjectURL(blob);

    const link = document.createElement('a');
    link.href = downloadUrl;
    link.download = `defaulters_${month}_${year}.csv`;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  const averageAttendance = () => {
    if (defaulters.length === 0) return 0;
    const total = defaulters.reduce((acc, s) => acc + parseFloat(s.attendance_percentage), 0);
    return (total / defaulters.length).toFixed(2);
  };

  const getColor = (percentage) => {
    if (percentage < 50) return 'red';
    if (percentage < 75) return 'orange';
    return 'green';
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h2>üìä Defaulter List</h2>

      <div style={{ margin: '1rem 0' }}>
        <label style={{ marginRight: '10px' }}>Month: </label>
        <select value={month} onChange={e => setMonth(parseInt(e.target.value))}>
          {months.map((m, index) => (
            <option key={index + 1} value={index + 1}>
              {m}
            </option>
          ))}
        </select>

        <label style={{ margin: '0 10px' }}>Year: </label>
        <select value={year} onChange={e => setYear(parseInt(e.target.value))}>
          {years.map((y, index) => (
            <option key={index} value={y}>
              {y}
            </option>
          ))}
        </select>

        <button
          onClick={downloadCSV}
          style={{
            marginLeft: '20px',
            padding: '5px 10px',
            backgroundColor: '#007bff',
            color: 'white',
            border: 'none',
            borderRadius: '5px',
            cursor: 'pointer',
          }}
        >
          ‚¨áÔ∏è Export CSV
        </button>
      </div>

      {loading ? (
        <p>Loading defaulters...</p>
      ) : defaulters.length === 0 ? (
        <p>No defaulters found for {months[month - 1]}, {year}.</p>
      ) : (
        <>
          <div style={{ marginBottom: '1rem', fontWeight: 'bold' }}>
            Total Defaulters: {defaulters.length} | Avg Attendance: {averageAttendance()}%
          </div>

          <div style={{ overflowX: 'auto' }}>
            <table border="1" cellPadding="10" style={{ borderCollapse: 'collapse', width: '100%' }}>
              <thead style={{ backgroundColor: '#f0f0f0' }}>
                <tr>
                  <th>GR No</th>
                  <th>Roll No</th>
                  <th>Name</th>
                  <th>Attendance %</th>
                </tr>
              </thead>
              <tbody>
                {defaulters.map((student, index) => (
                  <tr key={index}>
                    <td>{student.gr_no}</td>
                    <td>{student.roll_no}</td>
                    <td>{student.name}</td>
                    <td style={{ color: getColor(student.attendance_percentage) }}>
                      {student.attendance_percentage}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </>
      )}
    </div>
  );
};

export default DefaultersPage;
