import React, { useEffect, useState } from 'react';

const DivisionCat = () => {
  const [semesterData, setSemesterData] = useState([]);
  const [classData, setClassData] = useState([]);

  // Simulated navigation function
  const handleSubjectClick = (subjectCode) => {
    console.log(`Navigating to attendance for ${subjectCode}`);
    // In your actual app, this would be: navigate(`/attendance/${subjectCode}`);
  };

  useEffect(() => {
    // Simulated data fetching
    const fetchData = async () => {
      try {
        // Simulate API calls
        setSemesterData(['2024-Odd-Semester']);
        setClassData(['D6ADA']);
      } catch (error) {
        console.error('Error fetching data', error);
      }
    };
    fetchData();
  }, []);

  const subjects = [
    { code: 'CO321', name: 'Computer Organization', color: 'blue' },
    { code: 'CO215', name: 'Data Structures', color: 'purple' },
    { code: 'CS670', name: 'Machine Learning', color: 'green' }
  ];

  const styles = {
    container: {
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #f8fafc 0%, #dbeafe 100%)',
      padding: '24px'
    },
    maxWidthContainer: {
      maxWidth: '896px',
      margin: '0 auto'
    },
    header: {
      textAlign: 'center',
      marginBottom: '32px'
    },
    iconContainer: {
      display: 'inline-flex',
      alignItems: 'center',
      justifyContent: 'center',
      width: '64px',
      height: '64px',
      background: 'linear-gradient(45deg, #2563eb, #9333ea)',
      borderRadius: '16px',
      marginBottom: '16px',
      boxShadow: '0 10px 15px -3px rgba(0, 0, 0, 0.1)',
      color: 'white'
    },
    mainTitle: {
      fontSize: '36px',
      fontWeight: 'bold',
      color: '#111827',
      marginBottom: '8px',
      margin: 0
    },
    subtitle: {
      fontSize: '20px',
      color: '#4b5563',
      fontWeight: '500',
      margin: 0
    },
    divider: {
      width: '96px',
      height: '4px',
      background: 'linear-gradient(45deg, #2563eb, #9333ea)',
      margin: '16px auto 0',
      borderRadius: '2px'
    },
    formSection: {
      backgroundColor: 'white',
      borderRadius: '24px',
      boxShadow: '0 25px 50px -12px rgba(0, 0, 0, 0.1)',
      padding: '32px',
      marginBottom: '32px',
      border: '1px solid #f3f4f6'
    },
    sectionTitle: {
      fontSize: '24px',
      fontWeight: '600',
      color: '#1f2937',
      marginBottom: '24px',
      display: 'flex',
      alignItems: 'center',
      margin: '0 0 24px 0'
    },
    sectionIcon: {
      width: '24px',
      height: '24px',
      marginRight: '12px',
      color: '#2563eb'
    },
    formGrid: {
      display: 'grid',
      gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))',
      gap: '24px'
    },
    inputGroup: {
      display: 'flex',
      flexDirection: 'column'
    },
    label: {
      display: 'block',
      fontSize: '14px',
      fontWeight: '600',
      color: '#374151',
      marginBottom: '8px'
    },
    selectContainer: {
      position: 'relative'
    },
    select: {
      width: '100%',
      padding: '16px',
      backgroundColor: '#f9fafb',
      border: '2px solid #e5e7eb',
      borderRadius: '12px',
      appearance: 'none',
      cursor: 'pointer',
      color: '#374151',
      fontWeight: '500',
      fontSize: '16px',
      transition: 'all 0.2s ease',
      outline: 'none'
    },
    selectFocus: {
      borderColor: '#3b82f6',
      backgroundColor: 'white'
    },
    selectArrow: {
      position: 'absolute',
      right: '16px',
      top: '50%',
      transform: 'translateY(-50%)',
      width: '20px',
      height: '20px',
      color: '#9ca3af',
      pointerEvents: 'none'
    },
    subjectsGrid: {
      display: 'grid',
      gridTemplateColumns: 'repeat(auto-fit, minmax(280px, 1fr))',
      gap: '24px'
    },
    subjectCard: {
      position: 'relative',
      overflow: 'hidden',
      borderRadius: '16px',
      boxShadow: '0 10px 15px -3px rgba(0, 0, 0, 0.1)',
      cursor: 'pointer',
      transition: 'all 0.3s ease',
      transform: 'translateY(0)'
    },
    subjectCardHover: {
      boxShadow: '0 25px 50px -12px rgba(0, 0, 0, 0.25)',
      transform: 'translateY(-8px)'
    },
    subjectCardBlue: {
      background: 'linear-gradient(135deg, #3b82f6, #2563eb)',
      color: 'white'
    },
    subjectCardPurple: {
      background: 'linear-gradient(135deg, #8b5cf6, #7c3aed)',
      color: 'white'
    },
    subjectCardGreen: {
      background: 'linear-gradient(135deg, #10b981, #059669)',
      color: 'white'
    },
    subjectCardContent: {
      padding: '24px',
      position: 'relative',
      zIndex: 10
    },
    subjectCardBg: {
      position: 'absolute',
      top: 0,
      right: 0,
      width: '128px',
      height: '128px',
      borderRadius: '50%',
      backgroundColor: 'rgba(255, 255, 255, 0.1)',
      marginRight: '-64px',
      marginTop: '-64px'
    },
    subjectHeader: {
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'space-between',
      marginBottom: '16px'
    },
    subjectIconContainer: {
      width: '48px',
      height: '48px',
      backgroundColor: 'rgba(255, 255, 255, 0.2)',
      borderRadius: '12px',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center'
    },
    subjectIcon: {
      width: '24px',
      height: '24px',
      color: 'white'
    },
    arrowIcon: {
      width: '20px',
      height: '20px',
      color: 'white',
      opacity: 0.7,
      transition: 'opacity 0.3s ease'
    },
    arrowIconHover: {
      opacity: 1
    },
    subjectCode: {
      fontSize: '32px',
      fontWeight: 'bold',
      marginBottom: '8px',
      margin: '0 0 8px 0'
    },
    subjectName: {
      color: 'rgba(255, 255, 255, 0.9)',
      fontWeight: '500',
      margin: 0
    },
    subjectFooter: {
      marginTop: '16px',
      display: 'flex',
      alignItems: 'center',
      fontSize: '14px',
      color: 'rgba(255, 255, 255, 0.8)'
    },
    clockIcon: {
      width: '16px',
      height: '16px',
      marginRight: '8px'
    },
    footer: {
      textAlign: 'center',
      marginTop: '32px',
      color: '#6b7280'
    },
    footerText: {
      fontSize: '14px',
      margin: 0
    }
  };

  const getSubjectCardStyle = (color) => {
    const baseStyle = { ...styles.subjectCard };
    if (color === 'blue') return { ...baseStyle, ...styles.subjectCardBlue };
    if (color === 'purple') return { ...baseStyle, ...styles.subjectCardPurple };
    if (color === 'green') return { ...baseStyle, ...styles.subjectCardGreen };
    return baseStyle;
  };

  return (
    <div style={styles.container}>
      <div style={styles.maxWidthContainer}>
        {/* Header Section */}
        <div style={styles.header}>
          <div style={styles.iconContainer}>
            <svg style={styles.subjectIcon} fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6.253v13m0-13C10.832 5.477 9.246 5 7.5 5S4.168 5.477 3 6.253v13C4.168 18.477 5.754 18 7.5 18s3.332.477 4.5 1.253m0-13C13.168 5.477 14.754 5 16.5 5c1.746 0 3.332.477 4.5 1.253v13C19.832 18.477 18.246 18 16.5 18c-1.746 0-3.332.477-4.5 1.253" />
            </svg>
          </div>
          <h1 style={styles.mainTitle}>
            Academic Dashboard
          </h1>
          <p style={styles.subtitle}>
            Artificial Intelligence and Data Science
          </p>
          <div style={styles.divider}></div>
        </div>

        {/* Form Section */}
        <div style={styles.formSection}>
          <h2 style={styles.sectionTitle}>
            <svg style={styles.sectionIcon} fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M10.325 4.317c.426-1.756 2.924-1.756 3.35 0a1.724 1.724 0 002.573 1.066c1.543-.94 3.31.826 2.37 2.37a1.724 1.724 0 001.065 2.572c1.756.426 1.756 2.924 0 3.35a1.724 1.724 0 00-1.066 2.573c.94 1.543-.826 3.31-2.37 2.37a1.724 1.724 0 00-2.572 1.065c-.426 1.756-2.924 1.756-3.35 0a1.724 1.724 0 00-2.573-1.066c-1.543.94-3.31-.826-2.37-2.37a1.724 1.724 0 00-1.065-2.572c-1.756-.426-1.756-2.924 0-3.35a1.724 1.724 0 001.066-2.573c-.94-1.543.826-3.31 2.37-2.37.996.608 2.296.07 2.572-1.065z" />
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 12a3 3 0 11-6 0 3 3 0 016 0z" />
            </svg>
            Configuration
          </h2>
          
          <div style={styles.formGrid}>
            <div style={styles.inputGroup}>
              <label htmlFor="semester" style={styles.label}>
                Select Semester
              </label>
              <div style={styles.selectContainer}>
                <select 
                  id="semester" 
                  style={styles.select}
                  onFocus={(e) => Object.assign(e.target.style, styles.selectFocus)}
                  onBlur={(e) => Object.assign(e.target.style, styles.select)}
                >
                  <option>2024-Odd-Semester</option>
                </select>
                <svg style={styles.selectArrow} fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
                </svg>
              </div>
            </div>

            <div style={styles.inputGroup}>
              <label htmlFor="class" style={styles.label}>
                Select Class
              </label>
              <div style={styles.selectContainer}>
                <select 
                  id="class" 
                  style={styles.select}
                  onFocus={(e) => Object.assign(e.target.style, styles.selectFocus)}
                  onBlur={(e) => Object.assign(e.target.style, styles.select)}
                >
                  <option>D6ADA</option>
                </select>
                <svg style={styles.selectArrow} fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
                </svg>
              </div>
            </div>
          </div>
        </div>

        {/* Subjects Section */}
        <div style={styles.formSection}>
          <h2 style={styles.sectionTitle}>
            <svg style={{ ...styles.sectionIcon, color: '#9333ea' }} fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6.253v13m0-13C10.832 5.477 9.246 5 7.5 5S4.168 5.477 3 6.253v13C4.168 18.477 5.754 18 7.5 18s3.332.477 4.5 1.253m0-13C13.168 5.477 14.754 5 16.5 5c1.746 0 3.332.477 4.5 1.253v13C19.832 18.477 18.246 18 16.5 18c-1.746 0-3.332.477-4.5 1.253" />
            </svg>
            Subject Attendance
          </h2>
          
          <div style={styles.subjectsGrid}>
            {subjects.map((subject, index) => (
              <div
                key={subject.code}
                style={getSubjectCardStyle(subject.color)}
                onClick={() => handleSubjectClick(subject.code)}
                onMouseEnter={(e) => {
                  Object.assign(e.currentTarget.style, styles.subjectCardHover);
                  const arrow = e.currentTarget.querySelector('.arrow-icon');
                  if (arrow) Object.assign(arrow.style, styles.arrowIconHover);
                }}
                onMouseLeave={(e) => {
                  e.currentTarget.style.boxShadow = '0 10px 15px -3px rgba(0, 0, 0, 0.1)';
                  e.currentTarget.style.transform = 'translateY(0)';
                  const arrow = e.currentTarget.querySelector('.arrow-icon');
                  if (arrow) arrow.style.opacity = '0.7';
                }}
              >
                <div style={styles.subjectCardBg}></div>
                <div style={styles.subjectCardContent}>
                  <div style={styles.subjectHeader}>
                    <div style={styles.subjectIconContainer}>
                      <svg style={styles.subjectIcon} fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
                      </svg>
                    </div>
                    <svg className="arrow-icon" style={styles.arrowIcon} fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
                    </svg>
                  </div>
                  <h3 style={styles.subjectCode}>{subject.code}</h3>
                  <p style={styles.subjectName}>{subject.name}</p>
                  <div style={styles.subjectFooter}>
                    <svg style={styles.clockIcon} fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z" />
                    </svg>
                    View Attendance
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* Footer */}
        <div style={styles.footer}>
          <p style={styles.footerText}>Select a subject to view detailed attendance records</p>
        </div>
      </div>
    </div>
  );
};

export default DivisionCat;
